package navimateforbusiness

import navimateforbusiness.enums.Visibility

class LeadM {
    static mapWith = "mongo"

    // Auto generated by mongo driver
    String id

    // Creation / Update time
    String createTime
    String updateTime

    // TODO : Remove : ID from old lead database
    Long oldId

    // External ID for uploading through external sources
    String extId

    // Account of lead
    Long accountId

    // Uploader of lead
    Long ownerId

    // Visibility of Lead
    Visibility visibility

    // Remove Flag
    boolean isRemoved = false

    // Lead Name
    String name

    // Location
    double latitude
    double longitude
    String address

    // Template for uploading templated data
    Long templateId

    static constraints = {
        extId           nullable: true
        oldId           nullable: true
    }

    static mapping = {
        accountId index:true
        templateId index:true
    }
}
