package navimateforbusiness

class ProductM {
    static mapWith = "mongo"

    // Auto generated by mongo driver
    String id

    // Creation / Update time
    Date dateCreated
    Date lastUpdated

    // Account of Product
    Long accountId

    // Uploader of Product
    Long ownerId

    // Remove Flag
    boolean isRemoved = false

    // Product ID
    String productId

    // Product Name
    String name

    // Template for uploading templated data
    Long templateId

    static mapping = {
        accountId index:true
        templateId index:true
        autoTimestamp false
    }
}
